INSERT INTO `npc_text_localized` VALUES ('10842','ruRU','Говорящего с духами Джавада ищите на восточной стороне Большой кузни, это чуть к северу по восточной стороне. Все ясно?','','Говорящего с духами Джавада ищите на восточной стороне Большой кузни, это чуть к северу по восточной стороне. Все ясно?','','','','','','','','','','','','','');
